    private void TriCalcGoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_TriCalcGoActionPerformed
        double lado;
        lado=Double.parseDouble(ladoTri.getText());
        perimtxt.setText(String.valueOf(EntregaIndividualEj2.perimetro(lado)));
        alttxt.setText(String.valueOf(EntregaIndividualEj2.altura(lado)));
        areatxt.setText(String.valueOf(EntregaIndividualEj2.area(lado)));
    }//GEN-LAST:event_TriCalcGoActionPerformed

